package ng.edu.unnportal.com.lionapp;import ng.edu.unn.UnnParser.UnnParser.UnnFeed;import android.os.Parcel;import android.os.Parcelable;import java.util.List;public class Feed extends UnnFeed implements Parcelable {    private int data;    private List<UnnFeed> unnFeed;    public Feed(Parcel in){        data = in.readInt();    }    public Feed(List<UnnFeed> unnFeed){        this.unnFeed = unnFeed;    }    private static Parcelable.Creator<UnnFeed> CREATOR  =            new Parcelable.Creator<UnnFeed>(){                @Override                public UnnFeed[] newArray(int size){                     return new UnnFeed[size];                }                @Override                public Feed createFromParcel(Parcel in){                    return new Feed(in);                }            };    public List<UnnFeed> getUnnFeed(){        return unnFeed;    }    @Override    public int describeContents() {        return unnFeed.size();    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeInt(data);    }}